mapper.insert:

    select max(communityNumber)
    from fc_community

insert into fc_community(
    customerNumber, communityTitle, communityContent, communityVisitCount, communityImage
) values(
     #{customerNumber} , #{communityTitle}, #{communityContent} , 0 , #{communityImage}
)

insert into fc_communityDetailImage(
    communityId, multipleImage
) values(
    #{communityId}, #{multipleImage}
)

mapper.select:
select communityNumber, customerNumber, communityTitle, communityContent, communityVisitCount, CASE WHEN FLOOR(TIMESTAMPDIFF(MONTH, communityCreatedAt, NOW()) / 12) >= 1 THEN CONCAT(FLOOR(TIMESTAMPDIFF(MONTH, communityCreatedAt, NOW()) / 12), '년전') WHEN TIMESTAMPDIFF(MONTH, communityCreatedAt, NOW()) >= 1 THEN CONCAT(TIMESTAMPDIFF(MONTH, communityCreatedAt, NOW()), '달전') WHEN DATEDIFF(CURRENT_DATE, communityCreatedAt) >= 1 THEN CONCAT(DATEDIFF(CURRENT_DATE, communityCreatedAt), '일전') WHEN TIMESTAMPDIFF(HOUR, communityCreatedAt, NOW()) >= 1 THEN CONCAT(TIMESTAMPDIFF(HOUR, communityCreatedAt, NOW()), '시간전') ELSE CONCAT(TIMESTAMPDIFF(MINUTE, communityCreatedAt, NOW()), '분전') end as communityCreatedAt,
communityImage
from fc_community where communityNumber = #{communityNumber}

mapper.update:
update fc_community
set communityVisitCount = communityVisitCount + 1
where communityNumber = #{communityNumber}

update fc_community
set
    communityTitle = #{communityTitle},
    communityContent = #{communityContent}
where communityNumber = #{communityNumber}

mapper.insert:
insert into fc_communityComment(
    customerNumber, communityNumber, commentText
) values(
    #{customerNumber}, #{communityNumber}, #{commentText}
)

insert into fc_communityLike(
    communityNumber, customerNumber
) values (
    #{communityNumber}, #{customerNumber}
)

mapper.select:
select * from fc_communityLike
where communityNumber = #{communityNumber}
and customerNumber = #{customerNumber}

select count(*)
from fc_communityLike
where communityNumber = #{communityNumber}

select count(*)
from fc_communityComment
where communityNumber = #{communityNumber}

mapper.insert:
insert into fc_commentLikeStatus(
    customerNumber, commentNumber
) values(
    #{customerNumber}, #{commentNumber}
)

mapper.select:
select * from fc_commentLikeStatus
where customerNumber = #{customerNumber}
and commentNumber = #{commentNumber}

select count(*)
from fc_commentLikeStatus
where commentNumber = #{commentNumber}

mapper.insert:
insert into fc_communityCommentReply(
    customerNumber, commentNumber, communityCommentReplyText
) values(
    #{customerNumber}, #{commentNumber}, #{communityCommentReplyText}
)
